import React, { useState } from "react";

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [action, setAction] = useState('login');

    const handleFormSubmit = async (e) => {
        e.preventDefault();
        if (action === 'login') {
            console.log('Realizar login:', email, password);
            // Adicione aqui a lógica para lidar com o login
        } else if (action === 'voltar') {
            console.log('Voltar:', email, password);
            // Adicione aqui a lógica para lidar com a ação de voltar
        }
    }

    return ( 

    <div>
        <meta name="viewport" content="width=device-width, initial-scale=1.0" />
        <div className="login-form-wrap">
            <h2>Entrar</h2>
            <form className='login-form' onSubmit={handleFormSubmit}>
                <label htmlFor="email">Seu E-mail</label>
                <input
                    type="email"
                    id="email"
                    name="email"
                    placeholder="Seu Email"
                    required
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />

                <label htmlFor="password">Sua Senha</label>
                <input
                    type="password"
                    id="password"
                    name="password"
                    placeholder="Sua Senha"
                    required
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <h3>Esqueci a Senha </h3>

                <Button variant='primary' className='mainContainerBtn' onClick={() => navigate('/home')}> <Image src={SignUpIcon} fluid />Voltar</Button>
                <Button variant='primary' className='mainContainerBtn' onClick={() => navigate('/login')}> <Image src={LogInIcon} fluid />Entrar</Button>

            </form>
        </div>
     </div>   
    );
}

export default Login;
